import React,{useState,useEffect} from 'react';
import './HomeNavBar.css';
import './Header/Header.css';
import {Link} from 'react-router-dom';
import {AiOutlineDingding} from 'react-icons/ai';
import {useSelector,useDispatch} from 'react-redux';
import { FaShoppingCart } from "react-icons/fa";
import { FaUserCircle } from "react-icons/fa";
import {
    Form,
    FormControl,
    Nav,
    Navbar,
    NavDropdown,
  } from "react-bootstrap";


function HomeNavBar () {

    const[click,setClick] = useState(false);
      
    
    useEffect(() => {
      function setLength(){
        var len = 0;

        for(var i=0; i<cart.length;i++){
          if(userInfo?.name == cart[i].userName){
            len = len + 1
          }
        }
        setLen(len)
      }

      setLength();

    }, [userInfo]);
  

    return(
        <Navbar bg="dark" expand="lg" variant="dark">
        <Container>

        <div id='hdLogo'> 
                <img alt="Logo"  src={require("../images/Cafe.png")} width="90"  height="55" className="d-inline-block align-top" /> 
            </div>

          <Navbar.Brand>
          <Link to='/customer-home' className='homenavbar-logo'>
                  Cafe SecretAlly
          </Link>
          </Navbar.Brand>
  
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            
          <Nav className="m-auto">
            <Form inline>
              
            </Form>
          </Nav>
  
            {userInfo ? (
              <>
                <Nav>
                  <Nav.Link href="/myorders">My Orders</Nav.Link>
  
                  <Nav.Link href="/customer-home">
                          Home     
                </Nav.Link>

                <Nav.Link>
                  <Link to='/contact' className='homenav-links'>
                          Contact Us
                  </Link>
                </Nav.Link>
  
                <Nav.Link href="/cart">
                  
                                  <FaShoppingCart/> Cart <span className='circle' style={{position: 'absolute', top: '15px'}}>{length}</span>
                  
                </Nav.Link>
  
                  <NavDropdown title={userInfo?.name} id="basic-nav-dropdown">
                    <NavDropdown.Item href="/profile" style={{color:'black'}}>
                      <img
                        alt=""
                        src={`${userInfo.pic}`}
                        width="25"
                        height="25"
                        style={{ marginRight: 10 }}
                      />
                      MyProfile
                    </NavDropdown.Item>
  
                    <NavDropdown.Divider />
                    <NavDropdown.Item onClick={logoutHandler} style={{color:'black'}}>
                      Logout
                    </NavDropdown.Item>
                  </NavDropdown>
                </Nav>
              </>
            ) : (
              <Nav>
                {" "}
                <Nav.Link>
                <Link to='/customer-home' className='homenav-links'>
                          Home
                      </Link>
                </Nav.Link>
  
                <Nav.Link>
                  <Link to='/contact' className='homenav-links'>
                          Contact Us
                  </Link>
                </Nav.Link>
  
                
              </Nav>
              
            
             
            )}
          </Navbar.Collapse>
        </Container>
      </Navbar>
    );
}

export default HomeNavBar;
