import React, { useState } from "react";
import axios from "axios";
import './Feedback.css'
import HomeNavBar from "./HomeNavBar";






export default function AddComplaint() {


    const [first_name, setFname] = useState("");
    const [last_name, setLname] = useState("");
    const [email, setEmail] = useState("");
    const [complaint, setComplaint] = useState("");

    const sendData = async (e) => {
        e.preventDefault();

        let newComplaint = {
            first_name: first_name,
            last_name: last_name,
            email: email,
            complaint: complaint
        }

        axios.post("http://localhost:5000/customer/add", newComplaint).then(() => {
            alert("Complaint Added")


        }).catch((err) => {
            alert(err)
        })

        setFname("");
        setLname("");
        setEmail("");
        setComplaint("");

    }

    return (
        <>
            <HomeNavBar />
        
            <div className="container">
                <h2>Add Feedbacks</h2>
                         
                </div>

                <form onSubmit={sendData}>

                    <div className="col-md-7 element">
                        <label for="inser" className="form-label1">First Name</label>
                        <input type="text" name="productName" className="form-control input-field" id="inserProduct" placeholder="Enter first name" required
                            onChange={(e) => {
                                setFname(e.target.value);
                            }} />

                    </div>

                    <div className="col-md-7 element">
                        <label for="inserLname" className="form-label1">Last Name</label>
                        <input type="text" name="productName" className="form-control input-field" id="inserProduct" placeholder="Enter last name" required
                            onChange={(e) => {
                                setLname(e.target.value);
                            }} />
                    </div>
                    <div className="col-md-7 element">
                        <label for="inseremail" className="form-label1">Email Address</label>
                        <input type="text" name="productName" className="form-control input-field" id="inserProduct" placeholder="Enter email address" required
                            onChange={(e) => {
                                setEmail(e.target.value);
                            }} />
                    </div>
                    <div className="col-md-7 element">
                        <label for="inserfeedback" className="form-label1">Feedback</label>
                        <input type="text" name="productName" className="form-control input-field" id="inserProduct" placeholder="Enter feedback" required
                            onChange={(e) => {
                                setComplaint(e.target.value);
                            }} />
                    </div>
                    <div class="centerb"><button type="submit" className="btn btn-primary">Submit</button></div>
                </form>
            
            
        </>

    );
};
