import React, {useState,useEffect} from 'react';
import axios from 'axios';
import './LoadingPage.css'
import { Carousel } from 'react-bootstrap';


import image1 from './../assets/images/1.jpg';
import image2 from './../assets/images/2.jpg';
import image3 from './../assets/images/3.jpg';
import image4 from './../assets/images/4.jpg';
import image5 from './../assets/images/5.jpg';

export default function LoadingPage() {

    const handleproceedCus = () => {
        window.location.replace("http://localhost:3000/UserLogin");
    };

    const handleproceedAdmin = () => {
        window.location.replace("http://localhost:3000/AdminLogin");
    };

    
    return (
        <div className='body8'>
            <Carousel fade={true} pause={false}>

<Carousel.Item interval={2000}>
  <img
    className="d-block w-100"
    src={image1}
    alt="First slide"
  />
  <Carousel.Caption>
    <h3>First slide label</h3>
    <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
  </Carousel.Caption>
</Carousel.Item>

<Carousel.Item interval={2000}>
  <img
    className="d-block w-100"
    src={image2}
    alt="Second slide"
  />
  <Carousel.Caption>
    <h3>Second slide label</h3>
    <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
  </Carousel.Caption>
</Carousel.Item>

<Carousel.Item interval={2000}>
  <img
    className="d-block w-100"
    src={image3}
    alt="Third slide"
  />
  <Carousel.Caption>
    <h3>Third slide label</h3>
    <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
  </Carousel.Caption>
</Carousel.Item>

<Carousel.Item interval={2000}>
<img
    className="d-block w-100"
    src={image4}
    alt="Fourth slide"
  />
   <Carousel.Caption>
    <h3>Fourth slide label</h3>
    <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
  </Carousel.Caption>
</Carousel.Item>

<Carousel.Item interval={2000}>
<img
    className="d-block w-100"
    src={image5}
    alt="Fiveth slide"
  />
   <Carousel.Caption>
    <h3>Fiveth slide label</h3>
    <p>Praesent commodo cursus magna, vel scelerisque nisl consectetur.</p>
  </Carousel.Caption>
</Carousel.Item>

</Carousel>
)
        <center>
        <button type="button" onClick={()=>{handleproceedCus()}} style={{marginTop:'300px'}} className="btn btn-success">I am a Customer</button>
        <button type="button" onClick={()=>{handleproceedAdmin()}} style={{ marginTop:'300px', marginLeft: "60px" }} className="btn btn-danger">I am an Admin</button>
        </center>

        
        </div>


    )
}
