import React, { useState, useEffect} from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import Swal from "sweetalert2";

export default function AllComplaints(){

    const [customers, setCustomers] = useState([]);

    useEffect(() =>{
            function getComplaints() {
                axios.get("http://localhost:5000/customer/").then((res)=>{
                    setCustomers(res.data);
                }).catch((err)=>{
                    alert(err.message);
                })
            }
            getComplaints();
    },[])

    function deleteComplaint(id){
        Swal.fire({
            title: 'Are You Sure?',
            text: 'Once deleted, You will not able to recover these details !',
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#30085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Delete'

        }).then((res)=>{
            if(res.isConfirmed)
            {axios.delete(`http://localhost:5000/customer/delete/${id}`);
            Swal.fire({
                title: 'Success!',
                text: 'Delete Complaint Successfully',
                icon: 'success',
                showConfirmButton: false,
                
        });}
    }).catch((err)=>{
        Swal.fire({
            title: 'Error!',
            text: "Couldn't delete your Details",
            icon: 'error',
    });
    });

    setTimeout(()=>{
        window.location.replace("http://localhost:5000/allc");
    },3000)

}
    return(
        <>
        <div class='container'>
            <br/>
            <br/>
            <br/>
            <table class="table">
                <thead class="thead-dark">
                    <tr>
                    <th scope="col">First Name</th>
                    <th scope="col">Last Name</th>
                    <th scope="col">Email</th>
                    <th scope="col">Complaint</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                {
                                        deliveries.map(deliveries =>
                                            <tr key={deliveries.deliveryId}>
                                                <td> {deliveries.deliveryId}</td>
                                                <td> {deliveries.customerName}</td>
                                                <td> {deliveries.orderID}</td>
                                                <td> {deliveries.customerContactNumber}</td>
                                                <td> {deliveries.deliveryAddress}</td>
                                                <td> {deliveries.noofOrders}</td>
                                                <td> {deliveries.driverName}</td>
                                                <td> {deliveries.driverContactNumber}</td>
                                                <td> {deliveries.deliveryDate}</td>

                                            </tr>
                                        )
                                    }
                </tbody>
                </table>
        </div>
        </>
    )

}
