import React, { useState } from "react";
import axios from "axios";
import './Feedback.css';


export default function AddFeedback() {

    const [first_name, setFname] = useState("");
    const [last_name, setLname] = useState("");
    const [email, setEmail] = useState("");
    const [complaint, setComplaint] = useState("");

    const sendData = async (e) => {
        e.preventDefault();

        let newComplaint = {
            first_name: first_name,
            last_name: last_name,
            email: email,
            complaint: complaint
        }

        axios.post("http://localhost:5000/customer/add", newComplaint).then(() => {
            alert("Complaint Added")


        }).catch((err) => {
            alert(err)
        })

        setFname("");
        setLname("");
        setEmail("");
        setComplaint("");

    }

    return (
        <>
            <div className="container">
                <div class="center"><h2>Add Feedbacks</h2></div>

                <form onSubmit={sendData}>

                <div className="col-md-7 element">
                        <label for="inserProduct" className="form-label1">Product Name</label>
                        <input type="text" name="productName" className="form-control input-field" id="inserProduct" placeholder="Enter product name" required
                        onChange={(e) => {
                            setFname(e.target.value);
                        }} />
                    
                    </div>
                    <div className="form">
                        <label for="first name">First Name</label>
                        <input type="text" className="input" id="fname" required placeholder="Enter First Name"
                            onChange={(e) => {
                                setFname(e.target.value);
                            }} />
                    </div>
                    <div className="form">
                        <label for="last name">Last Name</label>
                        <input type="text" className="input" id="lname" required placeholder="Enter Last Name"
                            onChange={(e) => {
                                setLname(e.target.value);
                            }} />
                    </div>
                    <div className="form">
                        <label for="Email">Email address</label>
                        <input type="email" className="input" id="Email1" required placeholder="Enter email"
                            onChange={(e) => {
                                setEmail(e.target.value);
                            }} />
                    </div>
                    <div className="form">
                        <label for="complaint">Feedback</label>
                        <input type="text" className="input" id="complaint" required placeholder="Enter Your Complaint"
                            onChange={(e) => {
                                setComplaint(e.target.value);
                            }} />
                    </div>
                    <div class="centerb"><button type="submit" className="btn btn-primary">Submit</button></div>
                </form>
            </div>
            </>

    )
}
